'use strict'

const fs = require('node:fs')

const read = fs.readFileSync
const readPromise = fs.promises.readFile

fs.readFileSync = (a, b) => {
   console.log('!!!INFORMATION FROM readFileSync: ', a, b)
   return read(a, b)
}
fs.promises.readFile = async (a, b) => {
   console.log('!!!INFORMATION FROM PROMISES.readFile: ', a, b)
   return await readPromise(a, b)
}